exports.api = function(req, res){
  var api = 'api'
    , sessID = req.headers.auth
    , body = {};
  if ( sessID === undefined ||
       sessID !== req.body.sessID ) {return res.end()};
  res.set('Content-Type', 'application/json');
  res.set('Auth', sessID);
  body.sessID = sessID;
//------------------------------------------------------

}

