#!/bin/env node

var smtp = require('../smtp');

var mail = {
  to: 'dimar@v30.amdocs.com',
  subject: 'TEST',
  html: '<b>Hello world ..</b>'
}

smtp.sendMail(mail, function(error, info){
    if(error){
        return console.log(error);
    }
    console.log('Message sent: ' + info.response);
});
