
var rtlLangsList = require('config').globals.rtlLangsList;

function MODULE(){
  this.lang = 'eng';

  this.init = function(lang){
    this.lang = lang;
    return this;
  }

  this.tr = function(string){
    return $DICT[string][this.lang];
  }

  this.direction = function() {
    if(rtlLangsList.indexOf(this.lang) > -1){return 'rtl'};
    return 'ltr';
  }
} // End of module

module.exports = new MODULE();

